mapper.select:
select CITY_NAME from M_CITY_TREE
where PARENT_NAME =
#{parentName}

select * from M_CITY_TREE where

	CITY_NAME IN
	
		#{cityName}

select CITY_ID from M_CITY_TREE
where CITY_NAME =
#{cityName}

select * from M_CITY_TREE where

	CITY_ID IN
	
		#{cityId}

mapper.update:
update M_CITY_TREE
set PARENT_ID = #{cityID}, PARENT_NAME = #{cityName}
where CITY_ID in 

	#{item.cityID}

mapper.select:
select

from M_CITY_TREE
where PARENT_ID
= #{parentID}

	and (CITY_NAME like concat('%', #{cityName}, '%') or
	CITY_PINYIN like concat('%', #{cityName}, '%'))


	and CITY_TYPE = #{cityType}


	and IS_VALID = #{isValid}

order by CITY_ID
limit #{startRecord}, #{pageSize}

select count(*)
from M_CITY_TREE
where PARENT_ID = #{parentID}

	and (CITY_NAME like concat('%', #{cityName}, '%') or
	CITY_PINYIN like concat('%', #{cityName}, '%'))


	and CITY_TYPE = #{cityType}


	and IS_VALID = #{isValid}

select CITY_ID
from M_CITY_TREE
where id = #{id}
